import React, { useState } from "react";
import "./index.css";

function StepIndicator({ step }) {
  const steps = [
    { nr: 1, label: "Welkom" },
    { nr: 2, label: "Gezin" },
    { nr: 3, label: "Doelen" },
  ];

  return (
    <div style={{ display: "flex", justifyContent: "center", marginBottom: "24px" }}>
      {steps.map((s) => (
        <div
          key={s.nr}
          style={{
            padding: "6px 16px",
            borderRadius: "999px",
            margin: "0 6px",
            background: step === s.nr ? "#1d4ed8" : "#e5e7eb",
            color: step === s.nr ? "#ffffff" : "#374151",
            fontWeight: 600,
            cursor: "default",
            fontSize: "0.9rem",
          }}
        >
          {s.nr}. {s.label}
        </div>
      ))}
    </div>
  );
}

function WelcomeStep({ onNext }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px" }}>Even kennismaken</h2>

      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Opvoedmaatje is er om jou snel en praktisch te ondersteunen.<br />
        Geen ingewikkelde woorden â€” gewoon iemand die met je meedenkt.
      </p>

      <ul style={{ color: "#4b5563", marginBottom: "24px", paddingLeft: "20px" }}>
        <li>Korte vragen over situaties thuis</li>
        <li>Concrete tips die je meteen kunt proberen</li>
        <li>Zien wat voor jou en je gezin werkt</li>
      </ul>

      <button
        onClick={onNext}
        style={{
          padding: "10px 18px",
          borderRadius: "999px",
          border: "none",
          background: "#1d4ed8",
          color: "#ffffff",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Doorgaan
      </button>
    </section>
  );
}

function FamilyStep({ onNext, onBack }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px" }}>Over jouw gezin</h2>

      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Later komt hier een echt formulier. Voor nu laten we alleen zien hoe de
        stappen werken.
      </p>

      <ul style={{ color: "#4b5563", marginBottom: "24px", paddingLeft: "20px" }}>
        <li>Leeftijd van je kind(eren)</li>
        <li>Waar je je nu vooral zorgen om maakt</li>
        <li>Wanneer de last het hoogst is (ochtend, avond, weekend)</li>
      </ul>

      <button
        onClick={onBack}
        style={{
          padding: "10px 18px",
          borderRadius: "999px",
          border: "none",
          marginRight: "12px",
          background: "#e5e7eb",
          color: "#111827",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Terug
      </button>

      <button
        onClick={onNext}
        style={{
          padding: "10px 18px",
          borderRadius: "999px",
          border: "none",
          background: "#1d4ed8",
          color: "#ffffff",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Verder
      </button>
    </section>
  );
}

function GoalStep({ onBack }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px" }}>Wat wil je bereiken?</h2>

      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Hier gaan we ouders later vragen naar hun doel, zodat Opvoedmaatje
        gerichte tips kan geven.
      </p>

      <ul style={{ color: "#4b5563", marginBottom: "24px", paddingLeft: "20px" }}>
        <li>Minder strijd rond naar bed gaan</li>
        <li>Rustiger ochtenden</li>
        <li>Meer plezier samen</li>
      </ul>

      <button
        onClick={onBack}
        style={{
          padding: "10px 18px",
          borderRadius: "999px",
          border: "none",
          marginRight: "12px",
          background: "#e5e7eb",
          color: "#111827",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Terug
      </button>

      <button
        style={{
          padding: "10px 18px",
          borderRadius: "999px",
          border: "none",
          background: "#10b981",
          color: "#ffffff",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        (Later) Start met Opvoedmaatje
      </button>
    </section>
  );
}

export default function App() {
  const [step, setStep] = useState(1);

  const next = () => setStep((s) => Math.min(s + 1, 3));
  const prev = () => setStep((s) => Math.max(s - 1, 1));

  return (
    <div
      style={{
        maxWidth: "960px",
        width: "100%",
        margin: "40px auto",
        background: "#ffffff",
        borderRadius: "16px",
        padding: "24px 24px 32px",
        boxShadow: "0 10px 30px rgba(15, 23, 42, 0.12)",
      }}
    >
      <header style={{ marginBottom: "24px" }}>
        <h1 style={{ margin: 0, fontSize: "1.8rem", color: "#1d4ed8" }}>
          Opvoedmaatje
        </h1>
        <p style={{ marginTop: "8px", color: "#4b5563" }}>
          Je doet je best. Soms is dat zwaar. Opvoedmaatje denkt met je mee.
        </p>
      </header>

      <StepIndicator step={step} />

      {step === 1 && <WelcomeStep onNext={next} />}
      {step === 2 && <FamilyStep onNext={next} onBack={prev} />}
      {step === 3 && <GoalStep onBack={prev} />}
    </div>
  );
}
