import React, { useState } from "react";
import "./index.css";

function App() {
  const [step, setStep] = useState(1);

  const next = () => setStep((s) => Math.min(s + 1, 3));
  const prev = () => setStep((s) => Math.max(s - 1, 1));

  return (
    <div
      style={{
        maxWidth: "960px",
        width: "100%",
        margin: "40px auto",
        background: "#ffffff",
        borderRadius: "16px",
        padding: "24px 24px 32px",
        boxShadow: "0 10px 30px rgba(15, 23, 42, 0.12)",
      }}
    >
      <header style={{ marginBottom: "24px" }}>
        <h1
          style={{
            margin: 0,
            fontSize: "1.8rem",
            color: "#1d4ed8",
          }}
        >
          Opvoedmaatje
        </h1>
        <p style={{ marginTop: "8px", color: "#4b5563" }}>
          Je doet je best. Soms is dat zwaar. Opvoedmaatje denkt met je mee.
        </p>
      </header>

      <main>
        <StepIndicator step={step} />

        {step === 1 && <WelcomeStep onNext={next} />}
        {step === 2 && <FamilyStep onNext={next} onBack={prev} />}
        {step === 3 && <GoalsStep onBack={prev} />}
      </main>
    </div>
  );
}

function StepIndicator({ step }) {
  const steps = [
    { nr: 1, label: "Welkom" },
    { nr: 2, label: "Gezin" },
    { nr: 3, label: "Doelen" },
  ];

  return (
    <div style={{ marginBottom: "24px", display: "flex", gap: "8px" }}>
      {steps.map((s) => (
        <div
          key={s.nr}
          style={{
            flex: 1,
            padding: "8px 12px",
            borderRadius: "999px",
            fontSize: "0.9rem",
            textAlign: "center",
            background: s.nr === step ? "#1d4ed8" : "#e5e7eb",
            color: s.nr === step ? "#ffffff" : "#374151",
            fontWeight: s.nr === step ? 600 : 400,
          }}
        >
          {s.nr}. {s.label}
        </div>
      ))}
    </div>
  );
}

function WelcomeStep({ onNext }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px", color: "#111827" }}>
        Even kennismaken
      </h2>
      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Opvoedmaatje is er voor korte, praktische steun. Geen oordeel, geen
        ingewikkelde woorden â€“ gewoon iemand die met je meedenkt.
      </p>

      <ul style={{ color: "#4b5563", marginBottom: "24px" }}>
        <li>Korte vragen stellen over situaties thuis</li>
        <li>Concrete tips krijgen die je meteen kunt proberen</li>
        <li>Zien wat voor jou en je gezin werkt</li>
      </ul>

      <button
        onClick={onNext}
        style={{
          padding: "10px 18px",
          borderRadius: "999px",
          border: "none",
          background: "#1d4ed8",
          color: "#ffffff",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Doorgaan
      </button>
    </section>
  );
}

function FamilyStep({ onNext, onBack }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px", color: "#111827" }}>
        Over jouw gezin
      </h2>
      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Later komt hier een echt formulier. Voor nu laten we alleen zien hoe de
        stappen werken.
      </p>

      <ul style={{ color: "#4b5563", marginBottom: "24px" }}>
        <li>Leeftijd van je kind(eren)</li>
        <li>Waar je je nu vooral zorgen om maakt</li>
        <li>Wanneer de last het hoogst is (ochtend, avond, weekend)</li>
      </ul>

      <div style={{ display: "flex", gap: "8px" }}>
        <button
          onClick={onBack}
          style={{
            padding: "10px 18px",
            borderRadius: "999px",
            border: "1px solid #d1d5db",
            background: "#ffffff",
            color: "#374151",
            cursor: "pointer",
          }}
        >
          Terug
        </button>
        <button
          onClick={onNext}
          style={{
            padding: "10px 18px",
            borderRadius: "999px",
            border: "none",
            background: "#1d4ed8",
            color: "#ffffff",
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          Verder
        </button>
      </div>
    </section>
  );
}

function GoalsStep({ onBack }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px", color: "#111827" }}>
        Wat wil je bereiken?
      </h2>
      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Hier gaan we ouders later vragen naar hun doel, zodat Opvoedmaatje
        gerichte tips kan geven.
      </p>

      <ul style={{ color: "#4b5563", marginBottom: "24px" }}>
        <li>Minder strijd rond naar bed gaan</li>
        <li>Rustiger ochtenden</li>
        <li>Meer plezier samen</li>
      </ul>

      <p style={{ marginBottom: "24px", color: "#4b5563" }}>
        Na deze onboarding kom je straks in de echte chat met Opvoedmaatje.
      </p>

      <div style={{ display: "flex", gap: "8px" }}>
        <button
          onClick={onBack}
          style={{
            padding: "10px 18px",
            borderRadius: "999px",
            border: "1px solid #d1d5db",
            background: "#ffffff",
            color: "#374151",
            cursor: "pointer",
          }}
        >
          Terug
        </button>
        <button
          style={{
            padding: "10px 18px",
            borderRadius: "999px",
            border: "none",
            background: "#10b981",
            color: "#ffffff",
            fontWeight: 600,
            cursor: "pointer",
          }}
        >
          (Later) Start met Opvoedmaatje
        </button>
      </div>
    </section>
  );
}

export default App;
