// ==============================
// Opvoedmaatje – App.js
// ==============================

import React, { useState } from "react";
import "./index.css";

// ==============================
// STAP-INDICATOR BOLLETJES
// ==============================
function StepIndicator({ step }) {
  const steps = [
    { nr: 1, label: "Welkom" },
    { nr: 2, label: "Gezin" },
    { nr: 3, label: "Doelen" },
  ];

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        marginBottom: "24px",
      }}
    >
      {steps.map((s) => (
        <div
          key={s.nr}
          style={{
            display: "flex",
            alignItems: "center",
            marginRight: s.nr === steps.length ? 0 : 8,
          }}
        >
          <span
            style={{
              padding: "6px 16px",
              borderRadius: "9999px",
              background: step === s.nr ? "#1d4ed8" : "#e5e7eb",
              color: step === s.nr ? "#ffffff" : "#374151",
              fontWeight: step === s.nr ? 600 : 400,
              fontSize: "0.9rem",
            }}
          >
            {s.nr}. {s.label}
          </span>
        </div>
      ))}
    </div>
  );
}

// ==============================
// WELCOME STEP
// ==============================
function WelcomeStep({ onNext }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px" }}>Even kennismaken</h2>
      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Opvoedmaatje is er om jou snel en praktisch te ondersteunen.
        Geen ingewikkelde woorden – gewoon iemand die met je meedenkt.
      </p>

      <ul
        style={{
          color: "#4b5563",
          marginBottom: "24px",
          paddingLeft: "20px",
        }}
      >
        <li>Korte vragen over situaties thuis</li>
        <li>Concrete tips die je meteen kunt proberen</li>
        <li>Zien wat voor jou en je gezin werkt</li>
      </ul>

      <button
        type="button"
        onClick={onNext}
        style={{
          padding: "10px 20px",
          borderRadius: "9999px",
          border: "none",
          background: "#1d4ed8",
          color: "#ffffff",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Doorgaan
      </button>
    </section>
  );
}

// ==============================
// FAMILY STEP
// ==============================
function FamilyStep({ onNext, onBack }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px" }}>Jouw gezin</h2>
      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Vertel in een paar woorden hoe jullie gezin eruitziet en waar je nu tegenaan loopt.
      </p>

      <button
        type="button"
        onClick={onBack}
        style={{
          padding: "8px 16px",
          marginRight: "8px",
          borderRadius: "9999px",
          border: "1px solid #d1d5db",
          background: "#ffffff",
          cursor: "pointer",
        }}
      >
        Terug
      </button>

      <button
        type="button"
        onClick={onNext}
        style={{
          padding: "8px 16px",
          borderRadius: "9999px",
          border: "none",
          background: "#1d4ed8",
          color: "#ffffff",
          fontWeight: 600,
          cursor: "pointer",
        }}
      >
        Volgende
      </button>
    </section>
  );
}

// ==============================
// GOAL STEP
// ==============================
function GoalStep({ onBack }) {
  return (
    <section>
      <h2 style={{ marginTop: 0, marginBottom: "8px" }}>Wat wil je bereiken?</h2>
      <p style={{ marginBottom: "16px", color: "#4b5563" }}>
        Denk aan één of twee concrete dingen die je graag anders zou willen zien thuis.
      </p>

      <button
        type="button"
        onClick={onBack}
        style={{
          padding: "8px 16px",
          borderRadius: "9999px",
          border: "1px solid #d1d5db",
          background: "#ffffff",
          cursor: "pointer",
        }}
      >
        Terug
      </button>

      {/* Later: knop om met de chatbot te starten */}
    </section>
  );
}

// ==============================
// HOOFDAPP
// ==============================
export default function App() {
  const [step, setStep] = useState(1);

  const next = () => setStep((s) => Math.min(s + 1, 3));
  const prev = () => setStep((s) => Math.max(s - 1, 1));

  return (
    <div
      style={{
        maxWidth: "960px",
        width: "100%",
        margin: "40px auto",
        background: "#ffffff",
        borderRadius: "16px",
        padding: "24px 24px 32px",
        boxShadow: "0 10px 30px rgba(15, 23, 42, 0.12)",
      }}
    >
      {/* HEADER: LOGO + TITEL + SUBTEKST */}
      <header style={{ marginBottom: "24px", textAlign: "center" }}>
        <img
          src="/logo-opvoedmaatje.png"
          alt="Opvoedmaatje logo"
          style={{ width: "120px", marginBottom: "12px" }}
        />
        <h1 style={{ margin: 0, fontSize: "1.8rem", color: "#1d4ed8" }}>
          Opvoedmaatje
        </h1>
        <p style={{ marginTop: "8px", color: "#4b5563" }}>
          Je doet je best. Soms is dat zwaar. Opvoedmaatje denkt met je mee.
        </p>
      </header>

      {/* ==============================
          STAP INDICATOR
          ============================== */}
      <StepIndicator step={step} />

      {/* ==============================
          STAPPEN LOGICA
          ============================== */}
      {step === 1 && <WelcomeStep onNext={next} />}
      {step === 2 && <FamilyStep onNext={next} onBack={prev} />}
      {step === 3 && <GoalStep onBack={prev} />}
    </div>
  );
}
